var route_follow = '';
var route_unfollow = '';


/* FUNCIÃ“N PARA SEGUIR USUARIOS Y DEJAR DE SEGUIR */
function followButtons() {
    $(".btn-follow").unbind("click").click(function () {
        $(this).addClass("hidden");
        $(this).parent().find(".btn-unfollow").removeClass("hidden");

        $.ajax({
            url: route_follow,//url guarda la ruta hacia donde se hace la peticion
            type: 'POST', // la variable type guarda el tipo de la peticion GET,POST,..
            data: {followed: $(this).attr("data-followed")},// data recive un objeto con la informacion que se enviara al servidor
            success: function (response) {//success es una funcion que se utiliza si el servidor retorna informacion
                console.log(response);
            }
        });
    });

    $(".btn-unfollow").unbind("click").click(function () {
        $(this).addClass("hidden");
        $(this).parent().find(".btn-follow").removeClass("hidden");

        $.ajax({
            url: route_unfollow, //url guarda la ruta hacia donde se hace la peticion
            type: 'POST', // la variable type guarda el tipo de la peticion GET,POST,..
            data: {followed: $(this).attr("data-followed")},// data recive un objeto con la informacion que se enviara al servidor
            success: function (response) { //success es una funcion que se utiliza si el servidor retorna informacion
                console.log(response);
            }
        });
    });

}
