var route_remove_user = '';
$(document).ready(function () {
    
});

function buttonsUsers() {
    /* FUNCIÓN AJAX PARA BORRAR USUARIOS...*******************/

    /* Incluimos el código javascript que nos permita mostrar los mensajes en globos */
    $(".btn-img").unbind("click").click(function () {
     $(this).parent().find('.pub-image').fadeToggle();
 });

    $(".btn btn-sm btn-danger btn-delete").unbind("click").click(function () {
     $(this).parent().parent().addClass('hidden');

     /* Damos funcionalidad al botón eliminar usuarios */
     $.ajax({
        url: route_remove_user + "/" + $(this).attr("data-id"),
        type: 'GET',
        success: function (response) {
            console.log(response);
            $("#ajax-notifications").removeClass('hidden').html(response);
        }
    });
 });
}
